package com.voya.training;

public class ForDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 10; i++) {
			System.out.println(2 * i);
		}

		System.out.println(" ");

		int x = 0;
		while (x < 10) {
			System.out.println(x);
			x++;
		}

		System.out.println(" ");

		int y = 0;
		do {
			System.out.println(y);
			y++;
		} while (y < 10);

	}

}
